INSERT INTO `tb_risk` (`id_risk`, `id_user`, `risk_issue`, `risk_issue_tambah`, `risk_issue_edit`, `tambah_user`, `edit_user`, `hapus_user`, `impact`, `likelihood`, `kontrol_mitigasi`, `irs`, `nk`, `risk_gabungan`) VALUES
	(1, 4, 'Instalasi dan Konfigurasi Perangkat Belum terdokumentasi dengan baik', NULL, '', 0, 0, 0, 2, 1, '<p>Mereview laporan pekerjaan dari building manager terhadap pelaksanaan tugas-tugas seluruh komponen building management.</p>', 2, 2, 2),
	(2, 12, 'Profil Risiko TI belum terukur secara akurat sehingga langkah mitigasi yang disiapkan tidak memadai', NULL, '', 0, 0, 0, 4, 4, '<ol><li>Memetakan seluruh resiko (critical path/point) daily operation di setiap bagian sehingga Risk Exposure Divisi OPT dapat diukur</li><li>Melakukan review berkala terhadap profile risiko divisi OPT, KDR sebagai koordinator</li><li>Memastikan analisa terhadap resiko operasional IT dilaksanakan secara kontinyu</li></ol>', 2, 2, 2),
	(3, 6, 'Terganggunya operasional rutin bank di seluruh uker BRI akibat kelemahan dalam pengelolaan dan kualitas operasional enterprise server (ES) ', NULL, '', 0, 0, 0, 0, 1, '<ol><li>Ketersediaan ketentuan mengenai pengelolaan operasional enterprise server</li><li>Pembinaan monitoring dan tindak lanjut oleh pejabat yang berwenang</li><li>Review dan pengkinian secara berkala</li></ol>', 3, 3, 3),
	(4, 6, 'Sebagian atau seluruh kegiatan operasional Data Center atau Data Recovery Center terhenti', NULL, '', 0, 0, 0, 2, 1, '<ol><li>Memperkuat monitoring (terhadap availability/ kesehatan perangkat, capacity planning dan ketersediaan product lifecycle perangkat (media) backup)</li><li>Maintenance secara rutin dan perbaikan (menghindari perangkat failed)</li><li>Menjaga Ketersediaan hot backup (redundant) dan simulasi/pengujian secara rutin</li><li>Pembinaan monitoring dan tindak lanjut oleh pejabat yang berwenang</li><li>Review dan pengkinian secara berkala</li><li>Mengimplementasikan design high availability server (virtualisasi, redundant power)</li><li>Memastikan mekanisme replikasi &amp; mirroring dalam performa yang handal</li><li>Memastikan kelengkapan dan keakuratan SOP</li></ol>', 2, 2, 2),
	(5, 12, 'Kegiatan perbaikan data (patching data) dilaksanakan tidak sesuai dengan permintaan', NULL, '', 0, 0, 0, 4, 4, '<ol><li>Memastikan terdapat SLA atas setiap proses patching yang dilakukan telah sesuai dengan ketentuan</li><li>Memastikan Kelengkapan dokumen penatakerjaan patching</li><li>Memastikan adanya dual control (pengawasan) dalam kegiatan patching data</li></ol>', 2, 2, 2),
	(6, 12, 'Kegiatan pengolahan data dan tindak lanjut audit tidak sesuai dengan permintaan', NULL, '', 0, 0, 0, 4, 4, '<p>Memastikan pengolahan data dan tindak lanjut Audit akurat, compliance dengan regulator dan sesuai target waktu yang ditetapkan</p>', 2, 3, 2.5),
	(7, 12, 'Fungsi Compliance Operasional TI tidak berjalan dengan baik', NULL, '', 0, 0, 0, 4, 4, '<p>Memastikan dokumentasi Fungsi Compliance Operasional TI ditata kelola dengan baik.</p>', 2, 3, 3),
	(8, 9, 'Sistem Aplikasi yang diimplementasikan tidak sesuai user requirement dan mengalami kegagalan di produksi.', NULL, '', 0, 0, 0, 1, 1, '<ol><li>Analisa BRS secara lengkap dan konfirmasi kepada user</li><li>Melaksanakan UAT bersama user/bisnis/divisi terkait untuk memastikan kesesuaian antara BRS, Aplikasi, dan Kebutuhan user</li></ol>', 2, 2, 2),
	(9, 10, 'Hasil kegiatan monitoring dan evaluasi yang dilakukan terhadap seluruh kegiatan/fungsi yang dilaksanakan tidak akurat/ optimal (contoh: success rate tidak sesuai target, respon time aplikasi lambat)', NULL, '', 0, 0, 0, 1, 2, '<p>1. Memastikan terdapat SLA atas setiap proses yang dilakukan</p><p>2. Memastikan setiap monitoring dan evaluasi yang dilakukan sesuai dengan prosedur</p><p>3. Pembinaan monitoring dan tindak lanjut oleh pejabat yang berwenang</p><p>4. Review dan pengkinian secara berkala</p>', 3, 3, 3),
	(10, 9, 'Kelemahan kepatuhan terhadap ketentuan SDLC dan Prosedur TI BRI', NULL, '', 0, 0, 0, 1, 3, '<ol><li>Memastikan dokumentasi testing lengkap dan sesuai SDLC</li><li>Melakukan pengelolaan repository dokumen testing dan source code / program</li></ol>', 4, 3, 3.5),
	(11, 9, 'Sistem yang dibutuhkan oleh user tidak dapat diselesaikan sesuai jadwal', NULL, '', 0, 0, 0, 2, 3, '<ol><li>Pengelolaan sumber daya testing secara optimal</li><li>Timeline testing yang wajar</li><li>Koordinasi dengan pihak internal dan eksternal secara efektif sehingga semua pihak dapat memenuhi target yang diinginkan.</li></ol>', 3, 3, 3),
	(12, 3, 'Infrastruktur Operasional Sistem Informasi Tidak memadai  (untuk ruang lingkup Kanwil, Kanins, Sendik, Corpu)', NULL, '', 0, 0, 0, 2, 2, '<ol><li>Memastikan terdapat SLA atas setiap proses yang dilakukan</li><li>Melakukan koordinasi dengan Uker/ pihak terkait</li><li>Pembinaan monitoring dan tindak lanjut oleh pejabat yang berwenang Review dan pengkinian infrastruktur secara berkala</li></ol>', 2, 3, 2.5),
	(13, 3, 'Infrastrukur operasional sistem informasi tidak dikinikan sehingga tidak dapat berfungsi dengan baik', NULL, '', 0, 0, 0, 3, 2, '<ol><li>Pembinaan monitoring dan tindak lanjut oleh pejabat yang berwenang Review dan pengkinian infrastruktur secara berkala</li><li>Melakukan koordinasi dengan Uker/ pihak terkait Melakukan analisis kebutuhan pengkinian infrastruktur sistem TI</li><li>Memastikan anggaran terkait kebutuhan pengadaan dan pengkinian telah dianggarkan dengan baik.</li></ol>', 2, 3, 2.5),
	(14, 3, 'Inefisiensi pengelolaan TI configuration karena terdapat komponen dan software yang tidak authorized (tidak berlabel/berlisensi) tidak diidentifikasi dan ditata kerjakan secara memadai', NULL, '', 0, 0, 0, 3, 2, '<ol><li>Ketersediaan ketentuan/standarisasi utk menidentifikasi &amp; menata kerjakan komponen &amp; software</li><li>Pembinaan monitoring dan tindak lanjut oleh pejabat yang berwenang</li><li>Review dan pengkinian infrastruktur secara berkala</li></ol>', 2, 4, 3),
	(15, 10, 'Problem ticket tidak dapat diselesaikan sesuai SLA', NULL, '', 0, 0, 0, 2, 2, '<p>1. Melakukan koordinasi dengan Uker/ pihak terkait</p><p>2. Memastikan pemenuhan jumlah resource (engineer, programmer, operator)</p><p>3. Pembuatan petunjuk troubleshoot yang lengkap</p><p>4. Memastikan resource yang ada memiliki skill yang dibutuhkan</p><p>5. Membuat tools untuk memonitoring penyelesaian problem tiket agar memenuhi SLA</p>', 2, 3, 2.5),
	(16, 7, 'Kerugian karena aset TSI dapat diakses oleh pihak eksternal ataupun pihak internal yang tidak berwenang/ilegal', NULL, '', 0, 0, 0, 0, 0, '<p>Penetration security test secara periodik</p>', 2, 3, 2.5),
	(17, 7, 'Kegiatan pemberian user akses terhadap sistem dan akses aplikasi dari unit kerja tidak sesuai dengan ketentuan', NULL, '', 0, 0, 0, 1, 1, '<ol><li>Melengkapi panduan operasional pemberian akses ke sistem dan aplikasi yang disesuaikan dengan kebijakan dan</li><li>Ketentuan pengamanan yang ada (Kebijakan Umum Sekuriti) Review dan pengkinian secara berkala</li></ol>', 2, 3, 2.5),
	(18, 7, 'Terdampaknya seluruh/ sebagian pada sistem TI BRI karena adanya kegagalan pada perangkat security', NULL, '', 0, 0, 0, 0, 0, '<ol><li>Mengimplemenetasikan design high availability untuk perangkat security TI</li><li>Memastikan adanya kontrak maintenance untuk semua perangkat security TI</li></ol>', 2, 3, 2.5),
	(19, 7, 'Terdampaknya server-server di Data Center karena serangan virus atau malware lainnya', NULL, '', 0, 0, 0, 0, 1, '<p>Memastikan instalasi endpoint security dan antivirus pada server-server Linux dan windows di Data Center</p>', 3, 3, 3),
	(20, 5, 'Adanya perubahan di aplikasi tanpa termonitor oleh bagian OPL (deployment, investigasi, problem solving , dll)', NULL, '', 0, 0, 0, 1, 1, '<p>Memastikan prosedur akses ke production dilaksanakan dengan disiplin, dan segala perubahan / log aktifitas termonitor dan tercatat dengan baik</p>', 2, 2, 2),
	(21, 5, 'Aplikasi mengalami masalah, availability Aplikasi tidak mencapai target 99.98%', NULL, '', 0, 0, 0, 2, 2, '<ol><li>Menganalisa potensi bug di aplikasi</li><li>Memperkuat monitoring terhadap scheduler aplikasi</li><li>Berkoordinasi dengan bagian terkait untuk permasalahan diluar error aplikasi (contoh: bagian Jaringan (div. SAT) untuk permasalahan Jaringan Komunikasi.</li><li>Memastikan SOP sudah akurat dan dijalankan dengan disiplin</li><li>Memastikan ketersediaan/ perpanjangan kontrak FMS (Facility management Services)</li><li>Memastikan pemenuhan skill operator FMS</li></ol>', 2, 2, 2),
	(22, 5, 'Terjadi human error dalam melaksanakan job operasional (operator)', NULL, '', 0, 0, 0, 1, 0, '<ol><li>Memastikan SOP dijalankan dengan disiplin</li><li>Memastikan hand over pekerjaan saat pergantian antar shift dilakukan dengan baik.</li></ol>', 2, 2, 2),
	(23, 5, 'Potensi gangguan yang terjadi dari aktifitas deployment pada existing aplikasi.', NULL, '', 0, 0, 0, 2, 2, '<p>Memastikan prosedur depolyment dilaksanakan dengan benar, termasuk adanya rollback plan, backup before jika diperlukan, kehadiran personel pengembang jika diperlukan.</p>', 2, 2, 2),
	(24, 5, 'Kegagalan proses EOD', NULL, '', 0, 0, 0, 0, 1, '<ol><li>Dilaksanakannya setiap tahapan proses batch sesuai ketentuan yang berlaku oleh petugas berwenang (worklist, eskalasi, reporting, dll)</li><li>Monitoring dan tindak lanjut oleh petugas yang berwenang</li><li>Memastikan kelengkapan dan keakuratan SOP</li></ol>', 3, 3, 3),
	(25, 5, 'Proses EOD memakan waktu lebih lama secarai teori menurut teknikal spesialis mesin core banking', NULL, '', 0, 0, 0, 0, 0, '<p>Melakukan analisa bersama teknikal support mesin core banking dan melakukan tunning untuk memperbaiki performa EOD</p>', 2, 2, 2),
	(26, 4, 'Kegagalan perangkat basic infrastrukur Data Center', NULL, '', 0, 0, 0, 0, 1, '<ol><li>Monitoring terhadap capacity planning dan ketersediaan product lifecycle perangkat &nbsp;backup</li><li>Maintenance secara rutin dan perbaikan</li><li>menjaga Ketersediaan hot backup (redundant) dan simulasi/pengujian secara rutin</li><li>Pembinaan monitoring dan tindak lanjut oleh pejabat yang berwenang&nbsp;</li><li>Review dan pengkinian secara berkala</li></ol>', 3, 3, 3),
	(27, 4, 'Kegagalan Mechanical/ Electrical di Gedung Data Center (DRC/GTI)', NULL, '', 0, 0, 0, 0, 1, '<ol><li>Memastikan Maintenance Agreement setiap perangkat.</li><li>Melakukan pengujian berkala M/E di Data Center (DRC/ GTI)</li><li>Memastikan kelengkapan SOP dan pelaksanaannya &nbsp;secara disiplin</li><li>Memastikan kompetensi personel building management dalam melaksanakan tugasnya, sesuai dengan SOP yang ada.</li></ol>', 3, 2, 2.5),
	(28, 8, 'Kegiatan  pengamanan formulir seperti formulir negosiasi, dan output lainnya seperti critical report, backup, kurang memadai', NULL, '', 0, 0, 0, 2, 1, '<ol><li>Pembinaan monitoring dan tindak lanjut oleh pejabat yang berwenang</li><li>Memastikan terdapat SLA atas setiap proses yang dilakukan</li><li>Memastikan setiap pekerjaan yang dilakukan telah sesuai dengan ketentuan yang berlaku</li></ol>', 2, 2, 2),
	(29, 8, 'Pengelolaan pengendalian dokumen tidak dilaksanakan dengan baik', NULL, '', 0, 0, 0, 3, 1, '<ol><li>Memastikan prosedur register surat dan dokumen lainnya dilaksanakan dengan baik</li><li>Memastikan prosedur pembuatan surat, disposisi, dilakukan dengan benar.</li><li>Melakukan monitoring terhadap Surat Keputusan terkait penatakerjaan dan kecepatan pencarian.</li></ol>', 1, 1, 1),
	(30, 8, 'Permasalahan pada pengadaan barang dan jasa', NULL, '', 0, 0, 0, 3, 4, '<ol><li>Pembuatan permohonan anggaran dilakukan dengan perhitungan yang benar dan mencukupi.</li><li>Verifikasi terhadap kelengkapan invoice dilaksanakan dengan cermat</li><li>Memastikan keakuratan data dalam membuat fiat bayar</li></ol>', 3, 3, 3),
	(31, 8, 'Permasalahan pada pelaksanaan program pendidikan', NULL, '', 0, 0, 0, 3, 3, '<ol><li>Verifikasi terhadap dokumen pengajuan pendidikan</li><li>Pengecekan tanggal, materi, provider, dan kuota peserta</li><li>Evaluasi hasil pendidikan berupa pengumpulan dokumen hasil pedidikan dari peserta.</li></ol>', 2, 2, 2),
	(32, 8, 'Permasalahan terkait formasi pekerja', NULL, '', 0, 0, 0, 2, 2, '<p>Membuat usulan pemenuhan pekerja baru sesuai dengan formasi yang telah ditetapkan oleh divisi CDS</p>', 2, 3, 2.5),
